mapper.select:
select Data from info where ((Hcode=#{minHcode} and Lcode>=#{minLcode})
	or (Hcode>#{minHcode} and Hcode<#{maxHcode})
	or (Hcode=#{maxHcode} and Lcode<=#{maxLcode})
	) ;

select Data from binarycolor where ((Hcode=#{minHcode} and Lcode>=#{minLcode})
	or (Hcode>#{minHcode} and Hcode<#{maxHcode})
	or (Hcode=#{maxHcode} and Lcode<=#{maxLcode})
	) ;

select sum(length(Data)) from binarycolor where ((Hcode=#{minHcode} and Lcode>=#{minLcode})
	or (Hcode>#{minHcode} and Hcode<#{maxHcode})
	or (Hcode=#{maxHcode} and Lcode<=#{maxLcode})
	) ;
